模拟 Array
public class Array {
    private int[] intArray;
    private int index;
    public int length;

    public Array(int max) {
        this.length = max;
        this.intArray = new int[max];
        this.index = 0;
    }

    // 向最后添加一个元素
    public void push(int value) {
        intArray[index] = value;
        index++;
    }

    // 最后一个元素删除
    public boolean pop() {
        // 后面的数据往前移
        index--;
        return true;
    }

    // 第一个元素删除
    public boolean shift() {
        for (int i = 1; i  intArray[j + 1]) {
                    int tmp = intArray[j];
                    intArray[j] = intArray[j + 1];
                    intArray[j + 1] = tmp;
                }
            }
        }
        this.show();
    }
}
